import Cart from './Cart';
import React from 'react';
import Navbar from './Navbar';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
        products: [
            {
                price: 999,
                title: 'Watch',
                qty: 1,
                img: 'data:image/jpeg;base64,',
                id: 1
            },
            {
                price: 9999,
                title: 'Mobile Phone',
                qty: 10,
                img: 'data:image/jpeg;base64,',
                id: 2
            },
            {
                price: 99999,
                title: 'Laptop',
                qty: 4,
                img: 'https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcTIXpdYLrcre6Qn8jIQy3XcJ2n_WK-d0C8ab_axxCPty8jPyg7ZVQEjPz3BHtVafDEktjfcX-audWeq&usqp=CAc',
                id: 3
            }
        ]
    }
  }

  handleIncreaseQty = (product) => {
      console.log('Hey please inc qty of ', product);
      const { products } = this.state;
      const index = products.indexOf(product);

      products[index].qty += 1;

      this.setState({
        // products:  products since key & value has same name, so we can write shorthand as below
        products
      })
  }

  handleDecreaseQty = (product) => {
      console.log('Hey please dec qty of ', product);
      const { products } = this.state;
      const index = products.indexOf(product);

      if (products[index].qty === 0) {
          return;
      }

      products[index].qty -= 1;
      this.setState({
        products
      })
  }

  handleDeleteProduct = (id) => {
      const { products } = this.state;
      const items = products.filter( (item) => item.id !== id);

      this.setState( {
        products: items
      })
  }

  getCartCount = () => {
    const { products } = this.state;
    let count = 0;

    products.forEach((product) => {
      count += product.qty;
    });

    return count;
  }

  getCartTotal = () => {
    const { products } = this.state;
    let cartTotal = 0;

    products.map((product) => {
      cartTotal += product.qty * product.price;
    });

    return cartTotal;
  }

  render() { 
    const { products } = this.state;
    return (
      <div className="App">
        <Navbar 
          count = { this.getCartCount() }
        />
        <Cart 
          products = {products} 
          onIncreaseQty = { this.handleIncreaseQty }
          onDecreaseQty = { this.handleDecreaseQty }
          onDeleteProduct = { this.handleDeleteProduct }
        />

        <div style={ { padding: 10, fontSize: 20 } }>
          TOTAL: { this.getCartTotal() }
        </div>
      </div>
    );
  }
}

export default App;
